import React from 'react';
import './Card.css';

const Card = ({ cards, choice }) => {
  const handleClick = () => {
    choice(cards);
  };
  return (
    <div className="card-grid">
      {cards.map((card) => (
        <div className="card" key={card.id}>
          <div>
            <img className="front" src={card.src} alt="front" />
            <img
              className="back"
              src="images/cover.png"
              onClick={handleClick}
              alt="back"
            />
          </div>
        </div>
      ))}
    </div>
  );
};

export default Card;
